import React, { Component } from 'react';

class Solution extends Component {

    spaces(word){
        let spaceArray = word.split("").map( s => " __ ")

        return(
            spaceArray
        )
    }

    hint(hintContent){
        return(
            hintContent
        )
    }

    render() {
        let tr = this.props.name
        return (
            <div>
                <div>{this.spaces(tr)}</div>
                <div>Hint: {this.hint("She is blond and cute and likes to poop alot. Also, she used to be a puppy once")}</div>               
            </div>
        );
    }
}

export default Solution;
