import React, { Component } from 'react';
import Letter from './Letter.js'

class Solution extends Component {

    // LettersToSpaces(word){
    //     let spaceArray = word.split("").map( s => " __ ")
    //     return (
    //         spaceArray
    //     )
    // }

    render() {
        // let spaceArray = this.LettersToSpaces(this.props.word)
        let wordArray = this.props.word.split("")
        return (
            <div>
                {wordArray.map(w => 
                    this.props.letterStatus[w] ?
                    <Letter letter={w} key={w}/> : 
                    <Letter letter={" __ "} key={w}/>
                )}
                <div>Hint: {this.props.hint}</div>               
            </div>
        );
    }
}

export default Solution;
