import React ,{Component} from 'react';
import './App.css';
import Solution from './components/Solution'
import Score from './components/Score'
import Letters from './components/Letters'


class App extends Component {

  constructor(){
    super()
    this.state = {
      letterStatus: this.generateLetterStatus(),
      solution: {
        word: "PHILLY",
        hint: "She is blond and cute and likes to poop alot. Also, she used to be a puppy once"
      },
      score: 100
    }

  }

  generateLetterStatus () {
    let letterStatus = {}
    for (let i= 65; i < 91; i ++){
      letterStatus[String.fromCharCode(i)] = false
    }
    return letterStatus
  }

  checkSolution (letter) {
    if (this.state.solution.word.indexOf(letter) > -1){
      return true
    }
    return false
  }

  selectLetter = (letter) => {
    console.log(letter)
    if (this.checkSolution(letter)){
      let letterStatus = this.state.letterStatus
      letterStatus[letter] = true
      this.setState({ letterStatus})
    }
  }

  render(){
    return (
      <div>
        <Score score={this.state.score}/>
        <Solution word={this.state.solution.word} hint={this.state.solution.hint} letterStatus={this.state.letterStatus}/>
        <Letters letterStatus={this.state.letterStatus} selectLetter={this.selectLetter}/>
      </div>
    )
  }

}

export default App;
