import React, { Component } from 'react';

class Solution extends Component {

    spaces(word){
        // let spaceArray = word.split("")
        let newS = word.split("").map( s => " __ ")
        // for (let i = 0; i< num; i ++){
        //     spaceArray.push(" __ ")
        // }
        return(
            newS
        )
    }

    hint(hintContent){
        return(
            hintContent
        )
    }

    render() {
        return (
            <div>
                <div>{this.spaces("Philly")}</div>
                <div>Hint: {this.hint("She is blond and cute and likes to poop alot. Also, she used to be a puppy once")}</div>               
            </div>
        );
    }
}

export default Solution;
