import React, { Component } from 'react';
import Letter from './Letter.js'

class Solution extends Component {

    LettersToSpaces(word){
        let spaceArray = word.split("").map( s => " __ ")
        return (
            spaceArray
        )
    }

    render() {
        let spaceArray = LettersToSpaces(this.props.word)
        return (
            <div>
                {spaceArray.map(w => <Letter letter={w} key={w}/>)}
                <div>Hint: {this.props.hint}</div>               
            </div>
        );
    }
}

export default Solution;
