import React, { Component } from 'react';
import Letter from './Letter.js'

class Solution extends Component {

    // spaces(word){
    //     let spaceArray = word.split("").map( s => " __ ")

    //     return(
    //         spaceArray
    //     )
    // }

    render() {
        let wordArray = this.props.word.split("")
        return (
            <div>
                {/* <div>{this.spaces(this.props.word)}</div> */}
                {wordArray.map(w => <Letter letter={w}/>)}
                <div>Hint: {this.props.hint}</div>               
            </div>
        );
    }
}

export default Solution;
