import React ,{Component} from 'react';
import './App.css';
import Solution from './components/Solution'
import Score from './components/Score'
import Letters from './components/Letters'


class App extends Component {

  constructor(){
    super()
    this.state = {
      letterStatus: {},
      solution: {
        word: "PHILLY",
        hint: "She is blond and cute and likes to poop alot. Also, she used to be a puppy once"
      },
      score: 100
    }

  }

  generateLetterStatus () {
    let letterStatus = {}
    for (let i= 65; i < 91; i ++){
      letterStatus[String.fromCharCode(i)] = false
    }
    return letterStatus
    this.setState({ letterStatus})
  }

  render(){

    return (
      <div>
        <Score score={this.state.score}/>
        <Solution word={this.state.solution.word} hint={this.state.solution.hint} letterStatus={this.state.letterStatus}/>
        <Letters letterStatus={this.state.letterStatus}/>
      </div>
    )
  }

}

export default App;
