import React, { Component } from 'react';

class Score extends Component {
    constructor(){
        super()
        this.state = {
            score : 100
        }
    }

    takeOffPoints(){
        this.state.score -= 20
    }

    addPoints(){
        this.state.score += 5
    }

    render() {
        return (
            <div>
                {this.state.score}        
            </div>
        );
    }
}

export default Score;
